sys_script_client.script:
function onLoad() {
    // Set the form fields with the values from g_scratchpad
    g_form.setValue('total_leaves', g_scratchpad.total_leaves);
    g_form.setValue('taken', g_scratchpad.taken_leaves);
    g_form.setValue('balance', g_scratchpad.balance);
}

function onChange(control, oldValue, newValue, isLoading, isTemplate) {
    if (isLoading || newValue === '') {
        return;
    }

    function calculateBalance() {
        var totalLeaves = parseFloat(g_form.getValue('total_leaves')) || 30;
        var leaveDuration = parseFloat(g_form.getValue("duration")) || 0;
        var takenLeaves = parseFloat(g_form.getValue("taken")) || 0;

        console.log("Leave Duration: " + leaveDuration);
        console.log("Taken Leaves: " + takenLeaves);
        console.log("Total Leaves: " + totalLeaves);

        var leaveTaken = takenLeaves + leaveDuration;
        var balance = totalLeaves - leaveTaken;

        console.log("Leave Taken: " + leaveTaken);
        console.log("Balance: " + balance);

        g_form.setValue('balance', balance);
        g_form.setValue('taken', leaveTaken);
    }

    calculateBalance();
}
