function onCellEdit(sysIDs, table, oldValues, newValue, callback) {
  var saveAndClose = true;
 //Type appropriate comment here, and begin script below
if (sysIDs && sysIDs.length > 0) {
    var sys_id = sysIDs[0];
    var gr = new GlideRecord(x_1210077_employ_0_employee_asset_management);
    if (gr.get(sys_id)) {
      if (newValue == '3') {
        gr.setValue('model', '');
        gr.update(); 
        alert("The model field has been hidden because the category value is 3.");
      } else {
        gr.setValue('model', 'some_default_value'); 
        gr.update();
      }
    }
}
 callback(saveAndClose); 
}
